import React, { useState } from "react";
import { CountdownCircleTimer } from "react-countdown-circle-timer";
import "./styles.css";

// Timer properties
const timerProps = {
  isPlaying: true,
  size: 120,
  strokeWidth: 6
};

// Render the time in a specific format
const renderTime = (time) => {
  return (
    <div className="time-wrapper">
      <div className="time">{time}</div>
    </div>
  );
};

// Get time in minutes and seconds for the timer
const getTimeMinutes = (time) => Math.floor(time / 60);
const getTimeSeconds = (time) => time % 60;

export default function App() {
  const [isWorkTime, setIsWorkTime] = useState(true); // To track if it's work or break time
  const [key, setKey] = useState(0); // Key to reset the timer

  // Duration for work and break periods in seconds
  const workDuration = 25 * 60;
  const breakDuration = 5 * 60;

  // Colors for the timer
  const workColor = "#EF798A";
  const breakColor = "#218380";

  // Start a new work or break period
  const startNewPeriod = (isWork) => {
    setIsWorkTime(isWork);
    setKey(prevKey => prevKey + 1); // Increment key to reset the timer
  };

  return (
    <div className="App">
      <CountdownCircleTimer
        {...timerProps}
        colors={isWorkTime ? workColor : breakColor}
        duration={isWorkTime ? workDuration : breakDuration}
        key={key}
        onComplete={() => {
          // When a period is complete, switch to the other period
          startNewPeriod(!isWorkTime);
          return { shouldRepeat: true }; // Repeat the countdown for the next period
        }}
      >
        {({ elapsedTime }) =>
          renderTime(
            `${getTimeMinutes(elapsedTime).toString().padStart(2, '0')}:${getTimeSeconds(elapsedTime).toString().padStart(2, '0')}`
          )
        }
      </CountdownCircleTimer>

      <div>{isWorkTime ? 'Work Time' : 'Break Time'}</div>

      {/* You can add control buttons here if you need them */}
    </div>
  );
}
