import React, { useState, useEffect } from 'react';
import { CountdownCircleTimer } from 'react-countdown-circle-timer';

const PomodoroTimerV2 = () => {
  const workTime = 0.5 * 60; // 25 minutes
  const breakTime = 5 * 60; // 5 minutes
  const [isPlaying, setIsPlaying] = useState(false);
  const [isWorkTime, setIsWorkTime] = useState(true); // To track if it's work time or break time
  const [key, setKey] = useState(0); // Key to reset the timer

  useEffect(() => {
    if (isWorkTime) {
      // Automatically start the work timer when the component mounts
      setIsPlaying(true);
    }
  }, [isWorkTime]);

  // Function to start the timer
  const startTimer = () => setIsPlaying(true);

  // Function to pause the timer
  const pauseTimer = () => setIsPlaying(false);

  // Function to reset the timer
  const resetTimer = () => {
    setIsPlaying(false);
    setIsWorkTime(true); // Reset to work time
    setKey(prevKey => prevKey + 1); // Increment the key to reset the timer
  };

  // Function to switch between work and break periods
  const switchTimer = () => {
    setIsWorkTime(!isWorkTime);
    setKey(prevKey => prevKey + 1);
  };

  return (
    <div className='pomodoro-timer-container'>
      <CountdownCircleTimer
      className
        key={key}
        isPlaying={isPlaying}
        duration={isWorkTime ? workTime : breakTime}
        colors={[['#EF798A', 0.33], ['#D14081', 0.33], ['#9D174D']]}
        onComplete={switchTimer} // When the timer completes, switch between work and break
      >
        {({ remainingTime }) => {
          const minutes = Math.floor(remainingTime / 60);
          const seconds = remainingTime % 60;
          return `${minutes}:${seconds.toString().padStart(2, '0')}`;
        }}
      </CountdownCircleTimer>

      <div>{isWorkTime ? 'Work Time' : 'Break Time'}</div>

      <button className="timer-btn" onClick={startTimer}>Start</button>
      <button className="timer-btn" onClick={pauseTimer}>Pause</button>
      <button className="timer-btn" onClick={resetTimer}>Reset</button>
    </div>
  );
};

export default PomodoroTimerV2;
